/* - Desaturate logo - P1 */
.pinning-header .main-header .logo {
    color: lightgrey;
}

/* - Use neutral color for progression bars - P1 */
/* On Homepage */
.progress-completed {
    background-color: white;
}
/* On Player */
.nfp .scrubber-container .current-progress {
    background: white;
}
.nfp .scrubber-container .scrubber-head {
    background: white;
}
.nfp .scrubber-container .track {
    background: #444;
}

/* - Remove full width suggestions - U1 */
.billboard-row, .lolomoBigRow{
    display: none !important
}

/* - Make homepage tiles more sober - U1 */
.title-card{
    transition: 400ms all ease;
    opacity:  0.8
}
.title-card.is-bob-open{
    opacity: 1
}

/* - Hide homepage tiles video autoplay - C3 */
.bob-container .VideoMerchPlayer{
    display: none !important;
    visibility: hidden;
}
.bob-actions-wrapper .global-supplemental-audio-toggle{
    display: none;
}

/* - Hide "featured" meta pannel, newsletter prompt, teams prompt - U3 */
.community-bulletin,
#newsletter-ad,
ol.nav-links>li.p6{
  display: none !important;
}
/* - Remove hot network questions - C2 */
#hot-network-questions{
  display: none !important;
}
/* - Hide badges and reputation from topbar - C3 */
.my-profile .-badges, .my-profile .-rep{
  display: none !important;
}

/* - Remove left content border */
#content{
  border: none;
}

/* - Make top bar's colors fit the background color - P1 */
.top-bar{
  border: none;
  background-color: white;
}
.top-bar._fixed {
  box-shadow: 0 0 .3em .3em white;
}
#question-header{
  border: none;
}

/* - Make the logo more discrete - P1 */
.-logo{
  filter: grayscale(1);
  opacity: 0.6;
}

/* - Make related questions colors less bright - U1 */
.show-votes .sidebar-linked .spacer > a:first-child .answer-votes, .show-votes .sidebar-related .spacer > a:first-child .answer-votes {
  color: slategrey;
  background-color: transparent;
